import React, { useState } from "react";
import CatCard from "./CatCard";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function MatchPage({cats}) {

    const[match, setMatch] = useState(false)

    function handleMatchUpdate() {
        fetch(`http://localhost:3000/cats/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "matchStatus": "matched"
            })
        })
            .then((r) => r.json())
            .then(() => setMatch(true))
    }

    function handleUnMatch() {
        fetch(`http://localhost:3000/cats/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "matchStatus": "null"
            })
        })
            .then((r) => r.json())
            .then(() => setMatch(false))
    }

    const matchedCatArray = cats.filter((cat) => {
        if (cat.matchStatus === "matched") {
            return cat
        }
    })

    return (
        <Container>
        <Row>
              {matchedCatArray.map((cat) => (
              <Col sm key={cat.id}>
                          <CatCard 
                              id={cat.id}
                              image={cat.url}
                              name={cat.name}
                              favoritemovie={cat.favoriteMovie}
                              age={cat.age}
                              pickupLine={cat.pickupLine}
                              match={match} 
                              setMatch={setMatch}
                              handleMatchUpdate={handleMatchUpdate}
                              handleUnMatch={handleUnMatch}
                          />
              </Col>
          ))}
        </Row>
      </Container>
    )
}


export default MatchPage
