import React, { useState } from "react";
import CatCard from "./CatCard";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function MatchPage({cats}) {
    const[newCatArray, setNewCatArray] = useState([])

    function onMatchPage(data) {
        const newCatArray = cats.map((cat) => {
            if (cat.id === data.id) {
                return cat
            }
        });
        setNewCatArray([...newCatArray, cat])
    }

    return (
        <Container>
        <Row>
              {newCatArray.map((cat) => (
              <Col sm key={cat.id}>

                          <CatCard 
                              id={cat.id}
                              image={cat.url}
                              name={cat.name}
                              favoritemovie={cat.favoriteMovie}
                              age={cat.age}
                              pickupLine={cat.pickupLine}
                              onMatchPage={onMatchPage}
                          />
              </Col>
          ))}
        </Row>
      </Container>
    )
}


export default MatchPage
