import React from "react";
import CatCard from "./CatCard";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function CatList({
    cats,
    setCats
  }) {
    function handleDeleted(deletedCat) {
        setCats(cats.filter((cat) => cat.id !== deletedCat));
      }

      function handleMatch(changedCat) {
        const newCatsArray = cats.map((cat) => {
          if (cat.id !== changedCat.id) {
            return cat
          }
        }
        );
        setCats(newCatsArray)
      }

  return (
    <Container>
      <Row>
            {newCatsArray.map((cat) => (
            <Col sm key={cat.id}>

                        <CatCard 
                            id={cat.id}
                            image={cat.url}
                            name={cat.name}
                            favoritemovie={cat.favoriteMovie}
                            age={cat.age}
                            pickupLine={cat.pickupLine}
                            matchedStatus={cat.matched}
                            handleDeleted={handleDeleted}
                            handleMatchChange={handleMatch}
                        />
            </Col>
        ))}
      </Row>
    </Container>
  );
}

export default CatList;
